import React from 'react'
import {Text, View, StyleSheet, TouchableOpacity, Image, ScrollView, FlatList, ImageBackground} from 'react-native'
import {Ionicons} from '@expo/vector-icons'
import { HeaderButtons, Item } from 'react-navigation-header-buttons'


import Input from '../components/TextInput'
import Card from '../components/Card'
import {CATEGORIES, DEALS, SHOPS} from '../data/dummy-data'
import GridTile from '../components/GridTile'
import HeaderButton from '../components/HeaderButton'
import ShopTile from '../components/ShopTile'

const HomeScreen = props=> {

    const renderGridItem = itemData=> {
        return (
            <GridTile title={itemData.item.title} imageUrl= {itemData.item.imageUrl} onSelect={()=> {
                props.navigation.navigate({routeName: 'Category', params: {
                    categoryId: itemData.item.id,
                }})
            }}/>
        )
    }

    const renderDealItem = itemData=> {
        return (
            <GridTile title={itemData.item.title} imageUrl= {itemData.item.imageUrl} style={styles.dealItem} onSelect={()=> {
                props.navigation.navigate({routeName: 'Deals', params: {
                    categoryId: itemData.item.id,
                }})
            }}/>
        )
    }

    const renderShopItem = itemData=> {
        return (
            <ShopTile title={itemData.item.title} imageUrl= {itemData.item.imageUrl} style={styles.shopItem} onSelect={()=> {
                props.navigation.navigate({routeName: 'Shops', params: {
                    seller: itemData.item.title,
                }})
            }}/>
        )
    }

    return (
        <View style={{backgroundColor: 'orange'}}>
        <View style={styles.screen}>
            <Card style={styles.imageSlide}>
                <ImageBackground
                    source={require('../assets/index.jpeg')} 
                    style={styles.image}
                    resizeMode= 'cover' 

                    style={{flex: 1,justifyContent: 'flex-end'}}
                >
                    <Text style={styles.text}>On Demand Delivery</Text>
                </ImageBackground>
            </Card>
            <Card style={styles.categories}>
                <View style={{ flexDirection: 'row', flex:1}}>
                    <Text style={{flex: 1, paddingLeft:5}}>categories</Text>
                    <Text style={{ flex: 1, textAlign: 'right', paddingRight: 9}}>view all</Text>
                </View>
                <FlatList
                    keyExtractor={(item,index)=> item.id}
                    data= {CATEGORIES}
                    renderItem={renderGridItem}
                    numColumns={5}
                />
            </Card>
            <Card style={styles.dealsContainer}>
                <View style={{ flexDirection: 'row', flex:1}}>
                    <Text style={{flex: 1, paddingLeft: 5}}>TODAY'S DEALS</Text>
                    <Text style={{ flex: 1, textAlign: 'right', paddingRight: 9}}>view all</Text>
                </View>
                <Text style={{paddingLeft: 5}}>06:02:40</Text>
                <FlatList
                    keyExtractor={(item,index)=> item.id}
                    data= {DEALS}
                    renderItem={renderDealItem}
                    numColumns={5}
                />
            </Card>
            <Card style={styles.imageSlide}>
            <View style={{ flexDirection: 'row', flex:1}}>
                <Text style={{flex: 1, paddingLeft: 5}}>Shops Near You</Text>
                <Text style={{ flex: 1, textAlign: 'right', paddingRight: 9}}>view all</Text>
            </View>
            <View style={styles.shopCards}>
            <FlatList
                    keyExtractor={(item,index)=> item.id}
                    data= {SHOPS}
                    renderItem={renderShopItem}
                    numColumns={5}
                />
            </View>
            </Card>
        </View>
        </View>
    )
}

HomeScreen.navigationOptions = navData =>{
    return {
        headerStyle: {
            backgroundColor: 'orange'
        },
        headerLeft: <View style={styles.headerButtons}>
                        <View style={styles.screen1}>
                            <Card style={styles.inputContainer}>
                                <HeaderButtons HeaderButtonComponent={HeaderButton}>
                                    <Item 
                                        title="Menu"
                                        iconName='ios-menu'
                                        iconSize={23}
                                        onPress={()=>{
                                            navData.navigation.toggleDrawer();
                                        }}
                                    />
                                </HeaderButtons>
                                <Input 
                                    style={styles.input} 
                                    blurOnSubmit 
                                    autoCorrect={false} 
                                    placeholder="I'm looking for"
                                    keyboardType="number-pad" 
                                    maxLength={2}
                                />
                                <HeaderButtons HeaderButtonComponent={HeaderButton}>
                                    <Item 
                                        title="Search"
                                        iconName='ios-search'
                                        onPress={()=>{}}
                                        iconSize={23}
                                    />
                                </HeaderButtons>
                            </Card>
                            <Card style={styles.notificationContainer}>
                                <HeaderButtons HeaderButtonComponent={HeaderButton}>
                                    <Item
                                        title="notification"
                                        iconName='md-notifications-outline'
                                        onPress={()=>{}}
                                        iconSize={20}
                                    />
                                </HeaderButtons>
                            </Card>
                        </View>
                    </View> ,
    }
}

const styles = StyleSheet.create({
    screen: {
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 15
    },
    imageSlide: {
        height: 150,
        width: '95%',
        overflow: 'hidden',
        margin: 9
    },
    categories: {
        height: 100,
        width: '95%',
        overflow: 'hidden',
        marginBottom: 5

    },
    image: {
       width: '100%'

    },
    categoryItem: {
        height: 65,
        backgroundColor: 'grey',
        width: 60,
        margin: 5,
        alignItems: 'center',
        justifyContent: 'center'
    },
    smallCards: {
        flexDirection: 'row'
    },
    dealsContainer: {
        height: 150,
        width: '95%',
        overflow: 'hidden'
    },
    dealCards: {
        flexDirection: 'row'
    },
    dealItem: {
        height: 90,
        backgroundColor: 'grey',
        width: 90,
        margin: 5,
    },
    shopCards: {
        flexDirection: 'row'
    },
    shopItem: {
        height: 110,
        backgroundColor: 'grey',
        width: 110,
        margin: 5
    },
    screen1: {
        flexDirection: 'row',
        flex: 1,
      },
    headerButtons: {
        flexDirection: 'row',
    },
    input: {
        width: 100,
        textAlign: 'center',
        flex: 1,
        margin: 5
    },
    inputContainer: {
        width: '80%',
        maxWidth: '95%',
        minWidth: 300,
        alignItems: 'center',
        marginTop: 10,
        marginLeft: 9,
        marginRight: 4,
        flexDirection: 'row',
        flex: 1
    },
    notificationContainer: {
      width: 50,
      justifyContent: 'center',
      marginTop: 10,
      alignItems: 'center'
    },
    text: {
         textAlign: 'right', 
         fontSize: 30, 
         color: 'white',
         backgroundColor:'rgba(0,0,0,0.5)',
         padding: 5
    }
})


export default HomeScreen
